package rfronteddu.java_experiments.problem.collinear;

import java.util.LinkedList;

public interface CollinearPoints
{
    /**
     * @return Returns a list of lines representing the biggest segments
     * generated by collinear points. Note that the BruteCollinearPoints implementation
     * may have duplicated segments since the points are considered 4 at a time.
     */
    LinkedList<Line> getLines();
}
